	.byte $10, $10, $10, $10, $10, $03, $83, $03, $03, $03, $03, $03, $C3, $43, $43, $43
	.byte $43, $43, $43, $00, $03, $08, $0D, $10, $0D, $08, $03, $04, $03, $00, $03, $04
