	BMI PRG005_AFE2	 ; If arrow platform is moving upward, jump to PRG005_AFE2

	LDY Level_ObjectID,X
	CPY #OBJ_ARROWANY
	BNE PRG005_AF98	 ; If this is not the multi-directional arrow platform, jump to PRG005_AF98

	CMP #$10
