	; Play the direction switch sound
	LDA #SND_MAPPATHMOVE
	STA Sound_QMap

	; Timer = 8
	LDA #$08
	STA Objects_Timer,X

	; Var4 = (Var4 + 1) % 3 (increment and capped 0 to 3)
	LDA <Objects_Var4,X
	ADD #$01
	AND #$03
	STA <Objects_Var4,X

