	.byte $A3, $A5, $A7, $A9	; Type 0 (Up)
	.byte $A3, $AB, $AD, $A9	; Type 1 (Left)
	.byte $A3, $A5, $A7, $A9	; Type 2 (Up)
	.byte $A9, $AD, $AB, $A3	; Type 3 (Right)
	.byte $A3, $AF, $AF, $A9	; Type 4 (mid-direction-change)

