	BEQ PRG005_B1A8	 ; If no collision, jump to PRG005_B1A8

	CMP Temp_VarNP0	
	BEQ PRG005_B14C	 ; If detection status hasn't changed from last frame, jump to PRG005_B14C

	LDA #$0C

	LDY <Player_Suit
	BEQ PRG005_B14A	 ; If Player is small, jump to PRG005_B14A

