	LSR A		
	LSR A		
	LSR A		
	LSR A		
	ORA <Temp_Var3	; Makes an assumption that the object's Y value is row-aligned (which it generally is)
	STA <Temp_Var3	; Temp_Var3 = (Temp_Var4 >> 4) | Temp_Var3
