	LDX Sprite_RAM+$99,Y
	STA Sprite_RAM+$99,Y
	TXA
	STA Sprite_RAM+$9D,Y
	LDA Sprite_RAM+$9A,Y
	EOR #$40
	STA Sprite_RAM+$9A,Y
	LDA Sprite_RAM+$9E,Y
	EOR #$40
	STA Sprite_RAM+$9E,Y

PRG011_B68B:
