	STA <Temp_Var8		; Temp_Var8 = all stars center Y

	JSR PRG011_SUB_B9D4	; Returns 1 or -1 in Temp_Var6

	LDA <Temp_Var6	
	BMI PRG011_B980	 	; If Temp_Var6 < 0, jump to PRG011_B980
