; LevelLoad_TS5
;
; Entry point for loading level layout data for Level_Tileset = 5
;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;
LevelLoad_TS5:
	LDY #00

PRG019_A40A:
	LDA #TILE5_SKY 	; Clear everything to sky
	JSR Tile_Mem_ClearB
	JSR Tile_Mem_ClearA
	CPY #$f0
	BNE PRG019_A40A

	JMP LevelLoad	; Begin actual level loading!

