	DEC <Temp_Var4		 ; Temp_Var4--
	BPL PRG019_A5D9	 	; While Temp_Var4 >= 0, loop!

	; Restore Map_Tile_Addr from backup
	LDA <Temp_Var1
	STA <Map_Tile_AddrL
