; LevelLoad_TS9
;
; Entry point for loading level layout data for Level_Tileset = 9
;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;
LevelLoad_TS9:
	LDY #$00

PRG020_A40A:
	LDA #TILE9_SKY
	JSR Tile_Mem_ClearB
	JSR Tile_Mem_ClearA
	CPY #$e0
	BNE PRG020_A40A

PRG020_A416:
	LDA #TILE9_BRICK_UM	; Used as standard ground
	JSR Tile_Mem_ClearB
	INY	
	CPY #$f0
	BNE PRG020_A416

	JMP LevelLoad	; Begin actual level loading!

