	; Go to next row by adding 16
	TYA
	ADD #16
	TAY
	LDA <Map_Tile_AddrH
	ADC #$00
	STA <Map_Tile_AddrH

	DEY		 ; Y-- previous column
	TYA	
	AND #$0f
	CMP #$0f
	BNE PRG020_AE6E	 ; If haven't crossed screen boundary, jump to PRG020_AE6E

	; Go to previous screen by subtracting $1B0
	LDA <Map_Tile_AddrL
	SUB #$b0
	STA <Map_Tile_AddrL
	LDA <Map_Tile_AddrH
	SBC #$01
	STA <Map_Tile_AddrH

	INY
	TYA
